def visible(mx, range, start):

    visible_mx = []
    f = []

    for l in mx:

        f = l * 2
        visible_mx.append(f[start: start + range])

    return visible_mx

def contact(l, r, y, rng):

        for i in range(rng[0], rng[1]):

            if int(l[int((y) / 12)][rng[0] + 1]) or int(r[int((y) / 12)][rng[0] + 1]):

                return True

            elif int(l[int((y) / 12) + 1][i]) or int(r[int((y) / 12) + 1][i]):

                return True

            elif int(l[int((y) / 12) + 2][i]) or int(r[int((y) / 12) + 2][i]):

                return True

        return False
